// KanbanTask.js
import React from "react";
import { useDrag } from "react-dnd";

const KanbanTask = ({ id, text }) => {
  const [{ isDragging }, drag] = useDrag({
    type: "TASK",
    item: { id, text },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  return (
    <div ref={drag} className={`kanban-task ${isDragging ? "dragging" : ""}`}>
      {text}
    </div>
  );
};

export default KanbanTask;
