import BlogPost from "./components/BlogPost";

function App() {
  const { i18n } = useTranslation();

  const languageDirection = i18n.dir();
  const containerStyles = { direction: languageDirection };

  const [darkMode, setDarkMode] = useState(false);
  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <div className={`App ${darkMode ? "dark-mode" : ""}`}>
      <Router>
        <ScrollToTop />
        <Navbar onToggleDarkMode={toggleDarkMode} darkMode={darkMode} />
        <div className="language-container" style={containerStyles}>
          <Routes>
            <Route path="/" element={<Home darkMode={darkMode} />} />
            <Route
              path="/skyzcrm-ניהול-קשרי-לקוחות/אודות-skyzcrm/"
              element={<AboutUs />}
            />

            {/* בלוג – רשימה + פוסט */}
            <Route path="/בלוג/" element={<Blogs />} />
            <Route path="/בלוג/:url/" element={<BlogPost />} />

            <Route
              path="/skyzcrm-ניהול-קשרי-לקוחות/צור-קשר/"
              element={<Contact />}
            />
            <Route
              path="/skyzcrm-ניהול-קשרי-לקוחות/אודות-skyzcrm/privacy-policy/"
              element={<PrivacyPolicy />}
            />
            <Route
              path="/skyzcrm-ניהול-קשרי-לקוחות/אודות-skyzcrm/terms-of-service/"
              element={<TermsOfService />}
            />
            <Route
              path="/skyzcrm-ניהול-קשרי-לקוחות/אודות-skyzcrm/cookie-policy/"
              element={<CookiePolicy />}
            />

            {/* Sales */}
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/" element={<Sales />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-לידים-4/" element={<LeadManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-הזדמנויות/" element={<OpportunityManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-מכירות/" element={<SalesManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-קשרים-מכירות/" element={<ContactManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-פגישות-מכירות/" element={<MeetingsManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ניהול-ביצועים-מכירות/" element={<PerformanceManagement />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/ענן-מכירות/" element={<SkyzSalesCloud />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/מובייל-מכירות/" element={<SkyzSalesMobile />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/שרשור-workflow-מכירות/" element={<SalesWorkflow />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/אינטגרציה-לדואר-אלקטרוני-מכירות/" element={<EmailIntegration />} />
            <Route path="/skyzcrm-ניהול-קשרי-לקוחות/skyzsales/שיתוף-מידע-וקבצים-מכירות/" element={<InformationSharing />} />
