import React from "react";

const KanbanTask = ({ task }) => {
  return (
    <div className="task-container">
      <h4 className="task-title">{task.title}</h4>
      <p className="task-paragraph">{task.paragraph}</p>
    </div>
  );
};

export default KanbanTask;
