import React, { useEffect, useState } from "react";

const ZOOM_STEPS = [100, 115, 130, 150]; // אחוזים

export default function AccessibilityWidget() {
  const [zoomIdx, setZoomIdx] = useState(0); // 0=100%
  const [highContrast, setHighContrast] = useState(false);

  // החלה/הסרת זום
  const applyZoom = (pct) => {
    const body = document.body;
    const scale = pct / 100;

    // דפדפנים תומכי zoom (Chrome/Edge/Safari)
    body.style.zoom = `${pct}%`;

    // פיירפוקס פָּלְבֶּק: scale על #root + התאמת רוחב
    document.documentElement.style.setProperty("--a11y-scale", scale);
    body.setAttribute("data-a11y-zoom", String(pct));
  };

  // הפעלת ניגודיות גבוהה
  const applyContrast = (on) => {
    document.body.classList.toggle("a11y-contrast-high", on);
  };

  // באתחול – ודא שהמצב מוחל
  useEffect(() => {
    applyZoom(ZOOM_STEPS[zoomIdx]);
    applyContrast(highContrast);
  }, []); // פעם אחת

  const increaseZoom = () => {
    setZoomIdx((i) => {
      const next = Math.min(i + 1, ZOOM_STEPS.length - 1);
      applyZoom(ZOOM_STEPS[next]);
      return next;
    });
  };

  const decreaseZoom = () => {
    setZoomIdx((i) => {
      const next = Math.max(i - 1, 0);
      applyZoom(ZOOM_STEPS[next]);
      return next;
    });
  };

  const resetZoom = () => {
    setZoomIdx(0);
    applyZoom(100);
  };

  const toggleContrast = () => {
    const next = !highContrast;
    setHighContrast(next);
    applyContrast(next);
  };

  return (
    <div className="a11y-widget" aria-label="Accessibility controls">
      <button onClick={decreaseZoom} aria-label="קטן טקסט">A-</button>
      <button onClick={increaseZoom} aria-label="הגדל טקסט">A+</button>
      <button onClick={resetZoom} aria-label="איפוס">↺</button>
      <button onClick={toggleContrast} aria-pressed={highContrast}>
        ניגודיות גבוהה
      </button>
    </div>
  );
}
