// export default Kanban;

// Kanban.js
// ... (other imports and components)

const Kanban = () => {
  const [kanbanData, setKanbanData] = useState({
    // ... (existing state structure)
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // Check if there is no valid destination
    if (!destination) {
      return;
    }

    // Check if the task was dropped in a different location
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    // Update the state based on the drag and drop result
    const newColumnOrder = [...kanbanData.columnOrder];
    newColumnOrder.splice(source.index, 1);
    newColumnOrder.splice(destination.index, 0, source.droppableId);

    const newColumns = {
      ...kanbanData.columns,
      [source.droppableId]: {
        ...kanbanData.columns[source.droppableId],
        taskIds: [...kanbanData.columns[source.droppableId].taskIds],
      },
      [destination.droppableId]: {
        ...kanbanData.columns[destination.droppableId],
        taskIds: [
          ...kanbanData.columns[destination.droppableId].taskIds,
          draggableId,
        ],
      },
    };

    setKanbanData({
      ...kanbanData,
      columns: newColumns,
      columnOrder: newColumnOrder,
    });
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        {kanbanData.columnOrder.map((columnId) => (
          <KanbanColumn
            key={columnId}
            column={kanbanData.columns[columnId]}
            tasks={kanbanData.tasks}
          />
        ))}
      </div>
    </DragDropContext>
  );
};

export default Kanban;
