import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import KanbanGroup from "./KanbanGroup";
import "../assets/scss/_skyz-demo.scss";

const Demo = () => {
  const initialData = {
    tasks: {
      // משימות לפי התמונה
      task1: { title: "קמפיין פרסומי", description: "להכין קמפיין פרסומי חדש ברשתות החברתיות לקידום מוצר חדש של החברה.", p: "", date: "12-12-23", people: "שירי כהן" },
      task2: { title: "פגישה", description: "לפגוש עם לקוח פוטנציאלי חדש ולספק לו הצעת מחיר.", p: "", date: "12-12-23", people: "דור אלון" },
      task3: { title: "העברת קוד", description: "להעביר את הקוד למפתחים אחרים לצורך בדיקה ואינטגרציה.", p: "", date: "12-12-24", people: "מוין בן איי" },
      task4: { title: "דוחות כספיים", description: "להכין דוחות כספיים חודשיים של החברה.", p: "", date: "12-12-24", people: "ענת כהן" },
      task5: { title: "בדיקת תקינות", description: "לבדוק את תקינות הציוד והתשתיות של החברה.", p: "", date: "15-01-24", people: "יוסי בן חמו" },
      task6: { title: "בעיות טכניות", description: "לפתור בעיות טכניות של לקוחות.", p: "", date: "13-01-24", people: "אבי לוי" },
      task7: { title: "גיוס חדש", description: "להכין פרופיל גיוס חדש למשרה פנויה בחברה.", p: "", date: "14-02-24", people: "אסף בן דוד" }
    },
    columns: {
      // קבוצת פתוח
      col1: { id: "col1", title: "מתוכן", subtitle: "", taskIds: ["task1", "task2"] },
      // קבוצת ביצוע
      col2: { id: "col2", title: "בהמתנה", subtitle: "", taskIds: ["task3", "task4", "task5"] },
      col3: { id: "col3", title: "בעבודה", subtitle: "", taskIds: ["task6", "task7"] },
      // קבוצת סגור
      col4: { id: "col4", title: "בוצע", subtitle: "", taskIds: [] },
      col5: { id: "col5", title: "מבוטל", subtitle: "", taskIds: [] }
    },
    groups: {
      group1: { id: "group1", title: "פתוח", className: "kanban-1", columnOrder: ["col1"] },
      group2: { id: "group2", title: "ביצוע", className: "kanban-2", columnOrder: ["col2", "col3"] },
      group3: { id: "group3", title: "סגור", className: "kanban-3", columnOrder: ["col4", "col5"] }
    },
    groupOrder: ["group1", "group2", "group3"]
  };

  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (destination.droppableId === source.droppableId && destination.index === source.index) return;

    const newData = { ...data };
    const sourceColumn = newData.columns[source.droppableId];
    const destinationColumn = newData.columns[destination.droppableId];

    if (sourceColumn === destinationColumn) {
      const newTaskIds = Array.from(sourceColumn.taskIds);
      newTaskIds.splice(source.index, 1);
      newTaskIds.splice(destination.index, 0, draggableId);
      sourceColumn.taskIds = newTaskIds;
    } else {
      const sourceTaskIds = Array.from(sourceColumn.taskIds);
      sourceTaskIds.splice(source.index, 1);

      const destTaskIds = Array.from(destinationColumn.taskIds);
      destTaskIds.splice(destination.index, 0, draggableId);

      sourceColumn.taskIds = sourceTaskIds;
      destinationColumn.taskIds = destTaskIds;
    }

    setData(newData);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban-container">
        {data.groupOrder.map((groupId) => {
          const group = data.groups[groupId];
          const columns = group.columnOrder.map((colId) => data.columns[colId]);
          return (
            <KanbanGroup
              key={group.id}
              group={group}
              columns={columns}
              tasks={data.tasks}
            />
          );
        })}
      </div>
    </DragDropContext>
  );
};

export default Demo;
