import { Droppable, DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ columnId, tasks, subtitle, titleClassName }) => {
  const column = tasks.columns[columnId];

  if (!column) {
    return null; // Handle the case where the column is not found
  }

  const onTaskDrop = (result) => {
    const { destination, source, draggableId } = result;

    // Check if the drop is within the same column
    if (destination && destination.droppableId === columnId) {
      const newTaskIds = Array.from(column.taskIds);
      newTaskIds.splice(source.index, 1); // Remove the task from the source position
      newTaskIds.splice(destination.index, 0, draggableId); // Insert the task at the destination position

      const newTasks = {
        ...tasks,
        columns: {
          ...tasks.columns,
          [columnId]: {
            ...column,
            taskIds: newTaskIds,
          },
        },
      };

      setTasks(newTasks);
    }
  };

  //   return (
  //     <div>
  //       <div className={`title ${titleClassName}`}>
  //         <h2>{column.title}</h2>
  //         <span className="material-symbols-outlined close-btn">close</span>
  //       </div>
  //       <Droppable droppableId={columnId} type="TASK">
  //         {(provided) => (
  //           <div
  //             ref={provided.innerRef}
  //             {...provided.droppableProps}
  //             className="kanban-column"
  //           >
  //             <h3>{subtitle}</h3>
  //             <div className="kanban-tasks-list">
  //               {column.taskIds.map((taskId, index) => (
  //                 <KanbanTask
  //                   key={taskId}
  //                   taskId={taskId}
  //                   task={tasks.tasks[taskId]}
  //                   index={index}
  //                 />
  //               ))}
  //             </div>
  //             {provided.placeholder}
  //           </div>
  //         )}
  //       </Droppable>
  //     </div>
  //   );
  // };

  return (
    <div>
      <div className={`title ${titleClassName}`}>
        <h2>{column.title}</h2>
        <span className="material-symbols-outlined">close</span>
      </div>
      <Droppable droppableId={columnId} type="COLUMN">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="kanban-column"
          >
            <h3>{subtitle}</h3>

            {/* First kanban-tasks-list */}
            <Droppable droppableId={`${columnId}-list1`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-tasks-list"
                >
                  {column.taskIds.map((taskId, index) => (
                    <KanbanTask
                      key={taskId}
                      taskId={taskId}
                      task={tasks.tasks[taskId]}
                      index={index}
                    />
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            {/* Second kanban-tasks-list */}
            <Droppable droppableId={`${columnId}-list2`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-tasks-list"
                >
                  {/* Render tasks for the second list */}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            {/* Third kanban-tasks-list */}
            <Droppable droppableId={`${columnId}-list3`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-tasks-list"
                >
                  {/* Render tasks for the third list */}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>

            {/* Handle task drop within the column */}
            <DragDropContext onDragEnd={onTaskDrop} />
          </div>
        )}
      </Droppable>
    </div>
  );
};

export default KanbanColumn;
