import { useEffect, useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [completed, setCompleted] = useState([]);
  const [incompleted, setInompleted] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/todos")
      .then((response) => response.json())
      .then((json) => {
        setCompleted(json.filter((task) => task.completed));
        setInompleted(json.filter((task) => !task.completed));
      });
  }, []);

  const handleDradEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (source.droppableId == destination.droppableId) return;

    //  REMOVE FROM SOURCE ARRAY
    if (source.droppableId == 2) {
      setCompleted(removeItemId(draggableId, completed));
    } else {
      setInompleted(removeItemId(draggableId, incompleted));
    }

    //  GET ITEM
    const task = findItemById(draggableId, [...incompleted, ...completed]);

      //  ADD ITEM
      if (destination.droppableId == 2) {
          setCompleted([])
      }
      
  };

  function findItemById(id, array) {
    return array.find((item) => item.id == id);
  }

  function removeItemId(id, array) {
    return array.filter((item) => item.id != id);
  }
  return (
    <DragDropContext onDragEnd={handleDradEnd}>
      <div className="kanban-container">
        <KanbanColumn title={"TO DO"} tasks={incompleted} id={"1"} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
