const Blog = ({ blogs }) => {
  return (
    <>
      {blogs.map((blog) => {
        const imgSrc = `${process.env.PUBLIC_URL}${
          blog.img?.startsWith('/') ? '' : '/'
        }${blog.img || ''}`;

        return (
          <div className="blog" key={blog.id}>
            <h3 className="blog-title">{blog.title}</h3>
            <div className="img-container">
              <img
                src={imgSrc}
                alt={blog.title}
                loading="lazy"
                decoding="async"
                onError={(e) => {
                  e.currentTarget.src = `${process.env.PUBLIC_URL}/blogs/placeholder.jpg`;
                }}
              />
            </div>
            <p>{blog.short}</p>
          </div>
        );
      })}
    </>
  );
};

export default Blog;
