// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [columnsData, setColumnsData] = useState({
    open: {
      todo: [
        {
          id: "task-1",
          title: "Task 1",
          description: "Description for Task 1",
          assignedTo: "User A",
        },
        {
          id: "task-2",
          title: "Task 2",
          description: "Description for Task 2",
          assignedTo: "User B",
        },
        {
          id: "task-3",
          title: "Task 3",
          description: "Description for Task 3",
          assignedTo: "User C",
        },
      ],
      inProgress: [
        {
          id: "task-4",
          title: "Task 4",
          description: "Description for Task 4",
          assignedTo: "User D",
        },
        {
          id: "task-5",
          title: "Task 5",
          description: "Description for Task 5",
          assignedTo: "User E",
        },
      ],
      done: [
        {
          id: "task-6",
          title: "Task 6",
          description: "Description for Task 6",
          assignedTo: "User F",
        },
      ],
    },
    ongoing: {
      todo: [
        {
          id: "task-7",
          title: "Task 7",
          description: "Description for Task 7",
          assignedTo: "User G",
        },
      ],
      inProgress: [
        {
          id: "task-8",
          title: "Task 8",
          description: "Description for Task 8",
          assignedTo: "User H",
        },
        {
          id: "task-9",
          title: "Task 9",
          description: "Description for Task 9",
          assignedTo: "User I",
        },
      ],
      done: [
        {
          id: "task-10",
          title: "Task 10",
          description: "Description for Task 10",
          assignedTo: "User J",
        },
        {
          id: "task-11",
          title: "Task 11",
          description: "Description for Task 11",
          assignedTo: "User K",
        },
      ],
    },
    close: {
      todo: [
        {
          id: "task-12",
          title: "Task 12",
          description: "Description for Task 12",
          assignedTo: "User L",
        },
      ],
      inProgress: [
        {
          id: "task-13",
          title: "Task 13",
          description: "Description for Task 13",
          assignedTo: "User M",
        },
        {
          id: "task-14",
          title: "Task 14",
          description: "Description for Task 14",
          assignedTo: "User N",
        },
      ],
      done: [
        {
          id: "task-15",
          title: "Task 15",
          description: "Description for Task 15",
          assignedTo: "User O",
        },
        {
          id: "task-16",
          title: "Task 16",
          description: "Description for Task 16",
          assignedTo: "User P",
        },
        {
          id: "task-17",
          title: "Task 17",
          description: "Description for Task 17",
          assignedTo: "User Q",
        },
      ],
    },
  });

const onDragEnd = (result) => {
  try {
    const { destination, source, draggableId } = result;

    // If there's no destination or the task was dropped back into its original position, do nothing
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Get the task list for the source and destination columns
    const sourceColumnId = source.droppableId[0];
    const sourceSubCategoryId = source.droppableId[1];
    const destinationColumnId = destination.droppableId[0];
    const destinationSubCategoryId = destination.droppableId[1];

    const sourceTasks =
      columnsData[sourceColumnId]?.[sourceSubCategoryId] || [];
    const destinationTasks =
      columnsData[destinationColumnId]?.[destinationSubCategoryId] || [];

    // Log the source tasks before the splice operation
    console.log("Source Tasks Before:", sourceTasks);

    // Find the task being dragged
    const draggedTaskIndex = sourceTasks.findIndex(
      (task) => task.id === draggableId
    );

    if (draggedTaskIndex === -1) {
      console.error(
        `Task with id ${draggableId} not found in source column.`,
        sourceTasks,
        destinationTasks
      );
      return;
    }

    // Remove the task from the source column
    const [draggedTask] = sourceTasks.splice(draggedTaskIndex, 1);

    // Log the source tasks after the splice operation
    console.log("Source Tasks After:", sourceTasks);

    // Insert the task into the destination column at the specified index
    destinationTasks.splice(destination.index, 0, draggedTask);

    // Update the state with the new task order
    setColumnsData((prevColumnsData) => ({
      ...prevColumnsData,
      [sourceColumnId]: {
        ...prevColumnsData[sourceColumnId],
        [sourceSubCategoryId]: sourceTasks,
      },
      [destinationColumnId]: {
        ...prevColumnsData[destinationColumnId],
        [destinationSubCategoryId]: destinationTasks,
      },
    }));
  } catch (error) {
    console.error("Error in onDragEnd:", error);
  }
};



  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" columnData={columnsData.open} />
        <KanbanColumn id="ongoing" columnData={columnsData.ongoing} />
        <KanbanColumn id="close" columnData={columnsData.close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
