// export default KanbanColumn;

const KanbanColumn = ({ columnId, tasks, subtitle, titleClassName }) => {
  const column = tasks.columns[columnId];
  if (!column || !column.taskIds || !column.title) {
    console.error(`Column with ID ${columnId} is missing required properties`);
    return null;
  }
  console.log(`Rendering KanbanColumn for columnId: ${columnId}`);

  return (
    <div>
      <div className={`title ${titleClassName}`}>
        <h2>{column.title}</h2>
        <span className="material-symbols-outlined">close</span>
      </div>
      <div className="kanban-container">
        {[1, 2, 3].map((listIndex) => (
          <Droppable
            key={listIndex}
            droppableId={`${columnId}-list-${listIndex}`}
            type="TASK"
          >
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className="kanban-column"
              >
                <h3>{subtitle}</h3>
                <div className="kanban-tasks-list">
                  {column.taskIds
                    .filter((taskId) => taskId.endsWith(`list-${listIndex}`))
                    .map((taskId, index) => (
                      <KanbanTask
                        key={taskId}
                        taskId={taskId}
                        task={tasks[taskId]} // Use tasks directly, assuming tasks is a plain object
                        index={index}
                      />
                    ))}
                </div>
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </div>
    </div>
  );
};

export default KanbanColumn;
