import { Draggable } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";

const KanbanTask = ({ taskId, task, index }) => {
  // Check if the task object is defined before accessing its properties
  if (!task) {
    return null; // or handle the case where the task is undefined
  }

  return (
    <Draggable draggableId={taskId} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="kanban-task"
        >
          <h4>{task.title}</h4>
          <p>{task.description}</p>
          {task.date && <p>Date: {task.date}</p>}{" "}
          {/* Check if date is defined before rendering */}
        </div>
      )}
    </Draggable>
  );
};

export default KanbanTask;
