        <span className="material-symbols-outlined">close</span>
      </div>
      <Droppable {...droppableProps}>
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="kanban-tasks-container"
          >
            {renderTaskList(column.taskIds)}
