import React from "react";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ title, tasks }) => {
  return (
    <div>
      <h2>{title}</h2>
      <div className="kanban-column">
        <h3>subtitle</h3>
        <div className="kanban-tasks-list">
          {tasks.map((task) => (
            <KanbanTask key={task.id} text={task.text} />
          ))}
        </div>
      </div>
    </div>
  );
};
