import React, { useState } from "react";
import KanbanColumn from "./KanbanColumn";

const KanbanGroup = ({ group, columns, tasks }) => {
  const [isMinimized, setIsMinimized] = useState(false);

  return (
    <div className={`kanban-group ${isMinimized ? "minimize" : ""}`}>
      {/* כותרת קבוצה */}
      <div
        className={`group-header ${group.className}`}
        onClick={() => setIsMinimized(!isMinimized)}
      >
        <span className="group-icon">🔗</span>
        <h2>{group.title}</h2>
        <span className="material-symbols-outlined close-btn">
          {isMinimized ? "expand_content" : "close"}
        </span>
      </div>

      {/* עמודות פנימיות */}
      <div className={`columns-container ${isMinimized ? "hidden" : ""}`}>
        {columns.map((col) => (
          <KanbanColumn
            key={col.id}
            column={col}  // שולחים את כל העמודה
            tasks={tasks}
          />
        ))}
      </div>
    </div>
  );
};

export default KanbanGroup;
