// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [columnsData, setColumnsData] = useState({
    open: {
      todo: [
        {
          id: "task-1",
          title: "Task 1",
          description: "Description for Task 1",
          assignedTo: "User A",
        },
        {
          id: "task-2",
          title: "Task 2",
          description: "Description for Task 2",
          assignedTo: "User B",
        },
        {
          id: "task-3",
          title: "Task 3",
          description: "Description for Task 3",
          assignedTo: "User C",
        },
      ],
      inProgress: [
        {
          id: "task-4",
          title: "Task 4",
          description: "Description for Task 4",
          assignedTo: "User D",
        },
        {
          id: "task-5",
          title: "Task 5",
          description: "Description for Task 5",
          assignedTo: "User E",
        },
      ],
      done: [
        {
          id: "task-6",
          title: "Task 6",
          description: "Description for Task 6",
          assignedTo: "User F",
        },
      ],
    },
    ongoing: {
      todo: [
        {
          id: "task-7",
          title: "Task 7",
          description: "Description for Task 7",
          assignedTo: "User G",
        },
      ],
      inProgress: [
        {
          id: "task-8",
          title: "Task 8",
          description: "Description for Task 8",
          assignedTo: "User H",
        },
        {
          id: "task-9",
          title: "Task 9",
          description: "Description for Task 9",
          assignedTo: "User I",
        },
      ],
      done: [
        {
          id: "task-10",
          title: "Task 10",
          description: "Description for Task 10",
          assignedTo: "User J",
        },
        {
          id: "task-11",
          title: "Task 11",
          description: "Description for Task 11",
          assignedTo: "User K",
        },
      ],
    },
    close: {
      todo: [
        {
          id: "task-12",
          title: "Task 12",
          description: "Description for Task 12",
          assignedTo: "User L",
        },
      ],
      inProgress: [
        {
          id: "task-13",
          title: "Task 13",
          description: "Description for Task 13",
          assignedTo: "User M",
        },
        {
          id: "task-14",
          title: "Task 14",
          description: "Description for Task 14",
          assignedTo: "User N",
        },
      ],
      done: [
        {
          id: "task-15",
          title: "Task 15",
          description: "Description for Task 15",
          assignedTo: "User O",
        },
        {
          id: "task-16",
          title: "Task 16",
          description: "Description for Task 16",
          assignedTo: "User P",
        },
        {
          id: "task-17",
          title: "Task 17",
          description: "Description for Task 17",
          assignedTo: "User Q",
        },
      ],
    },
  });

  const onDragEnd = (result) => {
    console.log(result); // Log the result object to inspect its structure

    const { destination, source, draggableId } = result;
    console.log("draggableId:", draggableId); // Log draggableId

    // Get the task list for the source column
    const sourceTasks =
      columnsData[source.droppableId[0]][source.droppableId[1]];
    console.log("sourceTasks:", sourceTasks); // Log sourceTasks

    // Declare destinationTasks variable
    let destinationTasks;

    // Check if there is a destination and it's different from the source
    if (
      destination &&
      (destination.droppableId !== source.droppableId ||
        destination.index !== source.index)
    ) {
      // Get the task list for the destination column
      destinationTasks =
        columnsData[destination.droppableId[0]][destination.droppableId[1]];
      console.log("destinationTasks:", destinationTasks); // Log destinationTasks
    }

    // Find the task being dragged
    const draggedTask = sourceTasks.find((task) => task.id === draggableId);
    console.log("draggedTask:", draggedTask); // Log draggedTask to inspect its structure

    if (!draggedTask) {
      console.error(`Task with id ${draggableId} not found in source column.`);
      return;
    }

    // Remove the task from the source column
    sourceTasks.splice(source.index, 1);

    // Insert the task into the destination column at the specified index
    destinationTasks.splice(destination.index, 0, draggedTask);

    // Update the state with the new task order
    setColumnsData({
      ...columnsData,
      [source.droppableId[0]]: {
        ...columnsData[source.droppableId[0]],
        [source.droppableId[1]]: sourceTasks,
      },
      [destination.droppableId[0]]: {
        ...columnsData[destination.droppableId[0]],
        [destination.droppableId[1]]: destinationTasks,
      },
    });
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" columnData={columnsData.open} />
        <KanbanColumn id="ongoing" columnData={columnsData.ongoing} />
        <KanbanColumn id="close" columnData={columnsData.close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
