const CampaignOptimization = () => {
  const [marketing, setMarketing] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/marketing")
      .then((response) => response.json())
      .then((json) => setBlogs(json));
  }, []);
  return (
    <>
      <div className="product-page-container">
        <div className="product-section-container">
          <div className="right">
            <h1>Campaign optimization</h1>
            {marketing.map((m) => {
              return (
                <div className="para" key={CampaignOptimization.id}>
                  <h2>{CampaignOptimization.title}</h2>
                  <p>{CampaignOptimization.content}</p>
                </div>
              );
            })}
          </div>
          <div className="left">
            <img src={img1} />
          </div>
        </div>
      </div>
    </>
  );
};

