import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [tasks, setTasks] = useState({
    columns: {
      columnId: {
        title: "פתוח",
        subtitle: "פוטנציאלי",
        taskLists: [
          { id: "list1", taskIds: ["task1", "task2", "task3"] },
          { id: "list2", taskIds: ["task10", "task11", "task12"] }, // Add more tasks here
        ],
      },
      columnId2: {
        title: "ביצוע",
        subtitle: "זכיה",
        taskLists: [
          { id: "list3", taskIds: ["task4", "task5", "task6"] },
          { id: "list4", taskIds: ["task13", "task14", "task15"] }, // Add more tasks here
        ],
      },
      columnId3: {
        title: "סגור",
        subtitle: "בוצע",
        taskLists: [
          { id: "list5", taskIds: ["task7", "task8", "task9"] },
          { id: "list6", taskIds: ["task16", "task17", "task18"] }, // Add more tasks here
        ],
      },
    },
    tasks: {
      // ... existing tasks
      // Add more tasks here with unique task IDs
    },
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    const newTasks = { ...tasks };
    const sourceColumn = newTasks.columns[source.droppableId];
    const destinationColumn = newTasks.columns[destination.droppableId];

    sourceColumn.taskIds.splice(source.index, 1);
    destinationColumn.taskIds.splice(destination.index, 0, draggableId);

    setTasks(newTasks);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        {Object.keys(tasks.columns).map((columnId) => (
          <KanbanColumn
            key={columnId}
            columnId={columnId}
            tasks={tasks}
            subtitle={tasks.columns[columnId].subtitle}
            titleClassName={`title-${columnId}`}
          />
        ))}
      </div>
    </DragDropContext>
  );
};

export default Kanban;
