import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import KanbanGroup from "./KanbanGroup";
import "../assets/scss/_skyz-demo.scss";

const Demo = () => {
  const initialData = {
    tasks: {
      task1: {
        category: "砖",
        title: "拽驻 驻专住",
        description: " 拽驻 驻专住 砖 专砖转转 专转转 拽 爪专 砖 砖 专.",
        date: "12-12-23",
        people: "砖专 "
      },
      task2: {
        category: "驻砖",
        title: "驻砖",
        description: "驻砖 注 拽 驻爪 砖 住驻拽  爪注转 专.",
        date: "12-12-23",
        people: "专 "
      },
      task3: {
        category: "驻转 转",
        title: "注专转 拽",
        description: "注专 转 拽 驻转 专 爪专 拽 专爪.",
        date: "12-12-24",
        people: "  "
      },
      task4: {
        category: "砖转",
        title: "转 住驻",
        description: " 转 住驻 砖 砖 专.",
        date: "12-12-24",
        people: "注转 "
      },
      task5: {
        category: "转驻注",
        title: "拽转 转拽转",
        description: "拽 转 转拽转 爪 转砖转转 砖 专.",
        date: "15-01-24",
        people: "住  "
      },
      task6: {
        category: "砖专转 拽转",
        title: "注转 转",
        description: "驻转专 注转 转 砖 拽转.",
        date: "13-01-24",
        people: " "
      },
      task7: {
        category: "砖 砖",
        title: "住 砖",
        description: " 驻专驻 住 砖 砖专 驻 专.",
        date: "14-02-24",
        people: "住祝  "
      }
    },
    columns: {
      col1: { id: "col1", title: "转", taskIds: ["task1", "task2"] },
      col2: { id: "col2", title: "转", taskIds: ["task3", "task4", "task5"] },
      col3: { id: "col3", title: "注", taskIds: ["task6", "task7"] },
      col4: { id: "col4", title: "爪注", taskIds: [] },
      col5: { id: "col5", title: "", taskIds: [] }
    },
    groups: {
      group1: {
        id: "group1",
        title: "驻转",
        icon: "",
        className: "kanban-1",
        columnOrder: ["col1"]
      },
      group2: {
        id: "group2",
        title: "爪注",
        icon: "",
        className: "kanban-2",
        columnOrder: ["col2", "col3"]
      },
      group3: {
        id: "group3",
        title: "住专",
        icon: "",
        className: "kanban-3",
        columnOrder: ["col4", "col5"]
      }
    },
    groupOrder: ["group1", "group2", "group3"]
  };

  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (destination.droppableId === source.droppableId && destination.index === source.index) return;

    const newData = { ...data };
    const sourceColumn = newData.columns[source.droppableId];
    const destinationColumn = newData.columns[destination.droppableId];

    if (sourceColumn === destinationColumn) {
      const newTaskIds = Array.from(sourceColumn.taskIds);
      newTaskIds.splice(source.index, 1);
      newTaskIds.splice(destination.index, 0, draggableId);
      sourceColumn.taskIds = newTaskIds;
    } else {
      const sourceTaskIds = Array.from(sourceColumn.taskIds);
      sourceTaskIds.splice(source.index, 1);

      const destTaskIds = Array.from(destinationColumn.taskIds);
      destTaskIds.splice(destination.index, 0, draggableId);

      sourceColumn.taskIds = sourceTaskIds;
      destinationColumn.taskIds = destTaskIds;
    }

    setData(newData);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban-container">
        {data.groupOrder.map((groupId) => {
          const group = data.groups[groupId];
          const columns = group.columnOrder.map((colId) => data.columns[colId]);
          return (
            <KanbanGroup
              key={group.id}
              group={group}
              columns={columns}
              tasks={data.tasks}
            />
          );
        })}
      </div>
    </DragDropContext>
  );
};

export default Demo;
