// KanbanColumn.js
import { Droppable } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ columnId, tasks, titleClassName }) => {
  const column = tasks.columns[columnId];

  if (!column) {
    return null;
  }

  return (
    <div>
      <div className={`title ${titleClassName}`}>
        <h2>{column.title}</h2>
        <span className="material-symbols-outlined">close</span>
      </div>
      {Object.keys(column.taskLists).map((listId) => (
        <Droppable key={listId} droppableId={listId} type="TASK">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className="kanban-column"
            >
              <h3>{column.taskLists[listId].subtitle}</h3>
              <div className="kanban-tasks-list">
                {column.taskLists[listId].taskIds.map((taskId, index) => (
                  <KanbanTask
                    key={taskId}
                    taskId={taskId}
                    task={tasks.tasks[taskId]}
                    index={index}
                  />
                ))}
              </div>
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      ))}
    </div>
  );
};

export default KanbanColumn;
