// KanbanColumn.js
import React from "react";
import { Droppable } from "react-beautiful-dnd";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ id, tasks }) => {
  return (
    <div className="kanban-column">
      <h2>{id}</h2>
      {Object.entries(tasks).map(([subcategoryId, subcategoryTasks]) => (
        <Droppable key={subcategoryId} droppableId={subcategoryId}>
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className="kanban-tasks-list"
            >
              <h3>{subcategoryId}</h3>
              {subcategoryTasks.map((task, index) => (
                <KanbanTask key={task.id} task={task} index={index} />
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      ))}
    </div>
  );
};

export default KanbanColumn;
