import React from "react";
import { useEffect, useState } from "react";

const imgs = [
  {
    id: 1,
    img: "../assets/imgs/blogs/1.jpg",
  },
  {
    id: 2,
    title: "Send timely newsletters with the click of a button",
    content:
      "Marketing newsletters help strengthen and maintain customer loyalty—send them through Skyz’s advanced email system. Design your newsletter to suit your organization’s needs with different templates that are provided. Enable collaborative content and production with your staff using an advanced document management system.",
  },
  {
    id: 3,
    title: "Up-selling and cross-selling will help you to increase revenues",
    content:
      "Increase your revenues by sending out successful messaging that promote up-sales and cross-sales. Pitch your company’s customer base with Skyz Marketing according to their interests and budget. With well thought out offers and increased customer communication, increase your revenue with less marketing expenditure.",
  },
];

const Blog = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/blogs")
      .then((response) => response.json())
      .then((json) => setBlogs(json));
  }, []);

  return (
    <>
      {blogs.map((blog) => (
        <div className="blog" key={blog.id}>
          <h3 className="blog-title">{blog.title}</h3>
          <div className="img-container">
            <img src={blog.img} />
          </div>
          <p>{blog.short}</p>
        </div>
      ))}
    </>
  );
};

export default Blog;
