import React from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = ({ initialData }) => {
  const [tasks, setTasks] = React.useState(initialData);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    const newTasks = { ...tasks };
    const sourceColumn = newTasks.columns[source.droppableId];
    const destinationColumn = newTasks.columns[destination.droppableId];

    sourceColumn.taskIds.splice(source.index, 1);
    destinationColumn.taskIds.splice(destination.index, 0, draggableId);

    setTasks(newTasks);
  };

  const getMainTitleClassName = (columnId) => `main-title title-${columnId}`;

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <div className={getMainTitleClassName("columnId")}>
          <h2>{tasks.columns.columnId.title}</h2>
          <span className="material-symbols-outlined status-icon">close</span>
        </div>
        <div className="columns-container">
          {Object.keys(tasks.columns).map((columnId) => (
            <KanbanColumn
              key={columnId}
              columnId={columnId}
              tasks={tasks}
              subtitle={tasks.columns[columnId].subtitle}
              titleClassName={getMainTitleClassName(columnId)}
            />
          ))}
        </div>
      </div>
    </DragDropContext>
  );
};

export default Kanban;
