import React from "react";
import KanbanColumn from "./KanbanColumn";

const KanbanGroup = ({ group, columns, tasks }) => {
  return (
    <div className="kanban-group">
      {/* כותרת של הקבוצה */}
      <div className={`group-header ${group.className}`}>
        <span className="group-icon">🔗</span>
        <h2>{group.title}</h2>
      </div>

      {/* עמודות בתוך הקבוצה */}
      <div className="columns-wrapper">
        {columns.map((column) => (
          <KanbanColumn
            key={column.id}
            column={column}
            tasks={tasks}
          />
        ))}
      </div>
    </div>
  );
};

export default KanbanGroup;
