import { Link } from "react-router-dom";

const Blog = ({ blogs, lang = "he" }) => {
  // const basePath = lang === "he" ? "/he/בלוג" : "/en/blog";
  const dir = lang === "he" ? "rtl" : "ltr";

  return (
    <>
      {blogs.map((blog) => (
        <div className="blog" key={blog.id} dir={dir}>
          <Link to={`${basePath}/${encodeURIComponent(blog.slug || blog.url)}`}>
            <h3 className="blog-title">{blog.title}</h3>
            <div className="img-container">
              <img src={blog.img} alt={blog.title} />
            </div>
            <p>{blog.short}</p>
          </Link>
        </div>
      ))}
    </>
  );
};

export default Blog;
