// KanbanColumn.js
import React from "react";
import { Droppable } from "react-beautiful-dnd";
import KanbanTask from "./KanbanTask";

const SubColumn = ({ title, tasks, droppableId }) => {
  return (
    <Droppable droppableId={droppableId} type="TASK">
      {(provided) => (
        <div ref={provided.innerRef} {...provided.droppableProps} className="sub-column">
          <h3>{title}</h3>
          <div className="kanban-tasks-list">
            {tasks.map((task, index) => (
              <KanbanTask key={task.id} task={task} index={index} />
            ))}
          </div>
          {provided.placeholder}
        </div>
      )}
    </Droppable>
  );
};

const KanbanColumn = ({ id, tasks }) => {
  return (
    <div className="kanban-column">
      <h2>{id}</h2>
      <div className="sub-columns">
        <SubColumn title="To Do" tasks={tasks.filter((task) => task.status === "To Do")} droppableId={`todo-${id}`} />
        <SubColumn
          title="In Progress"
          tasks={tasks.filter((task) => task.status === "In Progress")}
          droppableId={`inprogress-${id}`}
        />
        <SubColumn title="Done" tasks={tasks.filter((task) => task.status === "Done")} droppableId={`done-${id}`} />
      </div>
    </div>
  );
};

export default KanbanColumn;
