// export default KanbanColumn;

const KanbanColumn = ({ columnId, tasks, subtitle }) => {
  const column = tasks.columns[columnId];

  if (!column) {
    return null;
  }

  return (
    <div>
      <div className="title">
        <h2>{subtitle}</h2>
        <span className="material-symbols-outlined">close</span>
      </div>
      <Droppable droppableId={columnId} type="TASK">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="kanban-column"
          >
            {Object.keys(column.taskLists).map((listId) => (
              <div key={listId} className="kanban-tasks-list">
                <h3>{column.taskLists[listId].title}</h3>
                {column.taskLists[listId].taskIds.map((taskId, index) => (
                  <KanbanTask
                    key={taskId}
                    taskId={taskId}
                    task={tasks.tasks[taskId]}
                    index={index}
                  />
                ))}
              </div>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
};

export default KanbanColumn;
