import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import KanbanGroup from "./KanbanGroup";
import "../assets/scss/_skyz-demo.scss";

const Demo = () => {
  const initialData = {
    tasks: {
      task1: { title: "משימה", description: "קמפיין פרסומי", p: "להכין קמפיין פרסומי חדש ברשתות החברתיות לקידום מוצר חדש של החברה.", date: "12-12-23", sum: "$210,00", people: "שירלי כהן" },
      task2: { title: "משימה", description: "פגישה", p: "לפגוש עם לקוח פוטנציאלי חדש ולספק לו הצעת מחיר.", date: "12-12-23", sum: "$210,00", people: "דור אלון" },
      task4: { title: "פיתוח תוכנה", description: "העברת קוד", p: "להעביר את הקוד למפתחים אחרים לצורך בדיקה ואינטגרציה.", date: "12-12-24", people: "מורן בן ארי" },
      task5: { title: "שירות לקוחות", description: "בעיות טכניות", p: "לפתור בעיות טכניות של לקוחות.", date: "13-01-24", people: "אבי לוי" }
    },
    columns: {
      col1: { id: "col1", title: "מתוכנן", taskIds: ["task1", "task2"] },
      col2: { id: "col2", title: "בעבודה", taskIds: ["task5"] },
      col3: { id: "col3", title: "בהמתנה", taskIds: ["task4"] }
    },
    groups: {
      group1: { id: "group1", title: "פתוח", className: "kanban-1", columnOrder: ["col1"] },
      group2: { id: "group2", title: "ביצוע", className: "kanban-2", columnOrder: ["col2", "col3"] }
    },
    groupOrder: ["group1", "group2"]
  };

  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (destination.droppableId === source.droppableId && destination.index === source.index) return;

    const newData = { ...data };
    const sourceColumn = newData.columns[source.droppableId];
    const destinationColumn = newData.columns[destination.droppableId];

    sourceColumn.taskIds.splice(source.index, 1);
    destinationColumn.taskIds.splice(destination.index, 0, draggableId);

    setData(newData);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="skyz-demo">
        <div className="demo-container">
          <div className="skyz-demo__main">
            <div className="card-container">
              <div className="card">
                <div className="kanban-container">
                  {data.groupOrder.map((groupId) => {
                    const group = data.groups[groupId];
                    const columns = group.columnOrder.map((colId) => data.columns[colId]);
                    return (
                      <div className="kanban" key={group.id}>
                        <div className={`main-title ${group.className}`}>
                          <span></span>
                          <h2>
                            {group.title}
                            <span className="material-symbols-outlined touch-icon">touch_app</span>
                          </h2>
                          <span className="material-symbols-outlined close-btn">close</span>
                        </div>
                        <div className="columns-container">
                          {columns.map((col) => (
                            <KanbanGroup
                              key={col.id}
                              group={group}
                              columns={[col]}
                              tasks={data.tasks}
                            />
                          ))}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </DragDropContext>
  );
};

export default Demo;
