import React from "react";
import KanbanColumn from "./KanbanColumn";

const YourParentComponent = ({ tasks }) => {
  const categories = [
    { id: "category1", title: "Category 1" },
    { id: "category2", title: "Category 2" },
    { id: "category3", title: "Category 3" },
  ];

  return (
    <div>
      {categories.map((category) => (
        <div key={category.id}>
          <h2>{category.title}</h2>
          <div className="category-columns">
            <KanbanColumn
              columnId={`${category.id}-column1`}
              tasks={tasks}
              subtitle="Subtitle 1"
            />
            <KanbanColumn
              columnId={`${category.id}-column2`}
              tasks={tasks}
              subtitle="Subtitle 2"
            />
            {/* Add more columns within the category as needed */}
          </div>
        </div>
      ))}
    </div>
  );
};

export default YourParentComponent;
