// KanbanColumn.js
import React from "react";
import { Droppable } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ columnId, tasks }) => {
  const column = tasks[columnId];

  if (!column) {
    // Handle the case where the column is undefined
    return null;
  }

  return (
    <div>
      <h2>{column.title}</h2>
      <Droppable droppableId={columnId} type="TASK">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="kanban-column"
          >
            <h3>subtitle</h3>
            <div className="kanban-tasks-list">
              {column.taskIds.map((taskId, index) => (
                <KanbanTask key={taskId} taskId={taskId} index={index} />
              ))}
            </div>
            {provided.placeholder}
          </div>
        )}
      </Droppable>
      <div className="nested-kanban-columns">
        {/* Add three nested columns here */}
        <KanbanColumn columnId="nestedColumnId1" tasks={tasks} />
        <KanbanColumn columnId="nestedColumnId2" tasks={tasks} />
        <KanbanColumn columnId="nestedColumnId3" tasks={tasks} />
        <KanbanColumn columnId="nestedColumnId3" tasks={tasks} />
        {/* Add more nested columns as needed */}
      </div>
    </div>
  );
};

export default KanbanColumn;
