// export default KanbanColumn;

const KanbanColumn = ({ columnId, tasks, subtitle, titleClassName }) => {
  const column = tasks.columns[columnId];

  if (!column) {
    return null;
  }

  return (
    <div>
      <div className={`title ${titleClassName}`}>
        <h2>{column.title}</h2>
        <span className="material-symbols-outlined">close</span>
      </div>

      <div className="kanban-tasks-container">
        {Object.keys(column.lists).map((listId) => (
          <div key={listId} className="kanban-tasks-list">
            <h3>{subtitle}</h3>
            <Droppable droppableId={listId} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-column"
                >
                  {column.lists[listId].map((taskId, index) => (
                    <KanbanTask
                      key={taskId}
                      taskId={taskId}
                      task={tasks.tasks[taskId]}
                      index={index}
                    />
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanColumn;
