// export default Kanban;
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

// אפשר להגדיר כאן את initialData אם לא מגיע כ-prop
const defaultData = {
  tasks: {
    "task-1": { id: "task-1", content: "משימה 1" },
    "task-2": { id: "task-2", content: "משימה 2" },
    "task-3": { id: "task-3", content: "משימה 3" },
  },
  columns: {
    open: {
      id: "open",
      title: "פתוח",
      subtitle: "משימות חדשות",
      taskIds: ["task-1"],
    },
    inProgress: {
      id: "inProgress",
      title: "בתהליך",
      subtitle: "משימות פעילות",
      taskIds: ["task-2"],
    },
    closed: {
      id: "closed",
      title: "סגור",
      subtitle: "משימות שהושלמו",
      taskIds: ["task-3"],
    },
  },
};

const Kanban = ({ initialData = defaultData, mainTitleClass }) => {
  const [tasks, setTasks] = useState(initialData);
  const [isMinimized, setIsMinimized] = useState(false);

  // פונקציה לגרירה
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) return;

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const startColumn = tasks.columns[source.droppableId];
    const finishColumn = tasks.columns[destination.droppableId];

    // גרירה בתוך אותה עמודה
    if (startColumn === finishColumn) {
      const newTaskIds = Array.from(startColumn.taskIds);
      newTaskIds.splice(source.index, 1);
      newTaskIds.splice(destination.index, 0, draggableId);

      const newColumn = {
        ...startColumn,
        taskIds: newTaskIds,
      };

      const newState = {
        ...tasks,
        columns: {
          ...tasks.columns,
          [newColumn.id]: newColumn,
        },
      };

      setTasks(newState);
      return;
    }

    // גרירה לעמודה אחרת
    const startTaskIds = Array.from(startColumn.taskIds);
    startTaskIds.splice(source.index, 1);
    const newStart = {
      ...startColumn,
      taskIds: startTaskIds,
    };

    const finishTaskIds = Array.from(finishColumn.taskIds);
    finishTaskIds.splice(destination.index, 0, draggableId);
    const newFinish = {
      ...finishColumn,
      taskIds: finishTaskIds,
    };

    const newState = {
      ...tasks,
      columns: {
        ...tasks.columns,
        [newStart.id]: newStart,
        [newFinish.id]: newFinish,
      },
    };

    setTasks(newState);
  };

  const handleToggleMinimize = () => {
    setIsMinimized((prev) => !prev);
  };

  const mainTitle = "משימות קנבן";

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className={`kanban ${isMinimized ? "minimize" : ""}`}>
        <div
          className={`main-title ${mainTitleClass}`}
          onClick={handleToggleMinimize}
        >
          <span></span>
          <h2>{mainTitle}</h2>
          <span className="material-symbols-outlined close-btn">
            {isMinimized ? "expand_content" : "close"}
          </span>
        </div>
        <div className="columns-container">
          {Object.keys(tasks.columns).map((columnId) => (
            <KanbanColumn
              key={columnId}
              columnId={columnId}
              tasks={tasks}
              subtitle={tasks.columns[columnId].subtitle}
              titleClassName={`title-${columnId}`}
            />
          ))}
        </div>
      </div>
    </DragDropContext>
  );
};

export default Kanban;
