import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [tasks, setTasks] = useState({
    columns: {
      columnId: {
        title: "פתוח",
        subtitle: "פוטנציאלי",
        taskLists: {
          list1: {
            title: "List 1",
            taskIds: ["task1", "task2", "task3"],
          },
          list2: {
            title: "List 2",
            taskIds: ["task4", "task5", "task6"],
          },
          list3: {
            title: "List 3",
            taskIds: ["task7", "task8", "task9"],
          },
        },
      },
    },
    tasks: {
      task1: { title: "פוטנציאלי", description: "הזדמנות" },
      task2: { title: "Task 2", description: "Description 2" },
      task3: { title: "Task 3", description: "Description 3" },
      task4: { title: "Task 4", description: "Description 4" },
      task5: { title: "Task 5", description: "Description 5" },
      task6: { title: "Task 6", description: "Description 6" },
      task7: { title: "Task 7", description: "Description 7" },
      task8: { title: "Task 8", description: "Description 8" },
      task9: { title: "Task 9", description: "Description 9" },
    },
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    const newTasks = { ...tasks };
    const sourceList =
      newTasks.columns[source.droppableId].taskLists[source.droppableId];
    const destinationList =
      newTasks.columns[destination.droppableId].taskLists[
        destination.droppableId
      ];

    sourceList.taskIds.splice(source.index, 1);
    destinationList.taskIds.splice(destination.index, 0, draggableId);

    setTasks(newTasks);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        {Object.keys(tasks.columns).map((columnId) => (
          <KanbanColumn
            key={columnId}
            columnId={columnId}
            tasks={tasks}
            subtitle={tasks.columns[columnId].title}
          />
        ))}
      </div>
    </DragDropContext>
  );
};

export default Kanban;
