import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import KanbanGroup from "./KanbanGroup";
import "../assets/scss/_skyz-demo.scss";

const Demo = () => {
  const initialData = {
    tasks: {
      task1: { title: "הזדמנות", description: "עדכון שדה מקור...", p: "פרטים נוספים", date: "12/12/23", sum: "$210,00", people: "משה, דוד, שלמה" },
      task2: { title: "הזדמנות", description: "שדרוג גולדפלוס", p: "", date: "1/1/24", sum: "$210,00", people: "מיטב השקעות בע\"מ" },
      task3: { title: "הזדמנות", description: "גולדפלוס/סקייז", p: "", date: "12/12/24", sum: "$210,00" },
      task4: { title: "הזדמנות", description: "התאמות סקייז לעמותה", p: "", date: "12/12/24", sum: "$210,00" },
      task5: { title: "הזדמנות", description: "פיצול מסד נתונים", p: "", date: "12/12/24", sum: "$210,00" }
    },
    columns: {
      col1: { id: "col1", title: "פתוח", subtitle: "פוטנציאלי", taskIds: ["task1", "task2"] },
      col2: { id: "col2", title: "ביצוע", subtitle: "הצעה", taskIds: ["task3"] },
      col3: { id: "col3", title: "סגור", subtitle: "הזמנה", taskIds: ["task4"] },
      col4: { id: "col4", title: "בתהליך", subtitle: "זכיה", taskIds: ["task5"] }
    },
    groups: {
      group1: { id: "group1", title: "פתוח", className: "kanban-1", columnOrder: ["col1", "col2"] },
      group2: { id: "group2", title: "בתהליך", className: "kanban-2", columnOrder: ["col3"] },
      group3: { id: "group3", title: "סגור", className: "kanban-3", columnOrder: ["col4"] }
    },
    groupOrder: ["group1", "group2", "group3"]
  };

  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) return;

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) return;

    const newData = { ...data };
    const sourceColumn = newData.columns[source.droppableId];
    const destinationColumn = newData.columns[destination.droppableId];

    if (sourceColumn === destinationColumn) {
      const newTaskIds = Array.from(sourceColumn.taskIds);
      newTaskIds.splice(source.index, 1);
      newTaskIds.splice(destination.index, 0, draggableId);
      sourceColumn.taskIds = newTaskIds;
    } else {
      const sourceTaskIds = Array.from(sourceColumn.taskIds);
      sourceTaskIds.splice(source.index, 1);

      const destTaskIds = Array.from(destinationColumn.taskIds);
      destTaskIds.splice(destination.index, 0, draggableId);

      sourceColumn.taskIds = sourceTaskIds;
      destinationColumn.taskIds = destTaskIds;
    }

    setData(newData);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban-container">
        {data.groupOrder.map((groupId) => {
          const group = data.groups[groupId];
          const columns = group.columnOrder.map((colId) => data.columns[colId]);
          return (
            <KanbanGroup
              key={group.id}
              group={group}
              columns={columns}
              tasks={data.tasks}
            />
          );
        })}
      </div>
    </DragDropContext>
  );
};

export default Demo;
