// export default KanbanColumn;
// KanbanColumn.js
import React from "react";
import { Droppable } from "react-beautiful-dnd";
import KanbanTask from "./KanbanTask";

const SubColumn = ({ title, tasks }) => {
  return (
    <div className="sub-column">
      <h3>{title}</h3>
      <Droppable droppableId={title}>
        {(provided) => (
          <div ref={provided.innerRef} {...provided.droppableProps} className="kanban-tasks-list">
            {tasks.map((task, index) => (
              <KanbanTask key={task.id} task={task} index={index} />
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </div>
  );
};

const KanbanColumn = ({ id, tasks }) => {
  return (
    <div className="kanban-column">
      <h2>{id}</h2>
      <div className="sub-columns">
        <SubColumn title="To Do" tasks={tasks.filter((task) => task.status === "To Do")} />
        <SubColumn title="In Progress" tasks={tasks.filter((task) => task.status === "In Progress")} />
        <SubColumn title="Done" tasks={tasks.filter((task) => task.status === "Done")} />
      </div>
    </div>
  );
};

export default KanbanColumn;
