import React, { useRef } from "react";
import emailjs from "@emailjs/browser";

export const ContactForm = () => {
  //   const form = useRef();

  //   const sendEmail = (e) => {
  //     e.preventDefault();

  //     emailjs
  //       .sendForm(
  //         "service_ky4w1yd",
  //         "template_fa15r54",
  //         form.current,
  //         "rexvJSVw22UHGT0HS"
  //       )
  //       .then(
  //         (result) => {
  //           console.log(result.text);
  //         },
  //         (error) => {
  //           console.log(error.text);
  //         }
  //       );
  //   };

  const contactForm = document.getElementById("contact-form"),
    contactName = document.getElementById("contact-name"),
    contactEmail = document.getElementById("contact-email"),
    contactMessage = document.getElementById("contact-message"),
    contactPhone = document.getElementById("contact-phone"),
    contactOrganization = document.getElementById("contact-organization"),
    contactError = document.getElementById("contact-error");

  const sendEmail = (e) => {
    e.preventDefault();

    // Check if the field has a value
    if (
      contactName.value === "" ||
      contactEmail.value === "" ||
      contactMessage.value === "" ||
      contactPhone.value === "" ||
      contactOrganization.value === ""
    ) {
      // Add and remove color
      contactError.classList.remove("color-blue");
      contactError.classList.add("color-red");

      // Show message
      contactError.textContent = "Write all the input fields ðŸ“¥";
    } else {
      // serviceID - templateID - #form - publickey
      emailjs
        .sendForm(
          "service_ky4w1yd",
          "template_fa15r54",
          "#contact-form",
          "rexvJSVw22UHGT0HS"
        )
        .then(
          () => {
            // Show message and add color
            contactError.classList.add("color-blue");
            contactError.textContent = "Message sent âœ…";

            // Remove message after five seconds
            setTimeout(() => {
              contactError.textContent = "";
            }, 5000);
          },
          (error) => {
            alert("OOPS! SOMETHING HAS FAILED...", error);
          }
        );

      // To clear the input field
      contactName.value = "";
      contactEmail.value = "";
      contactMessage.value = "";
    }
  };
  //   contactForm.addEventListener("submit", sendEmail);

  return (
    <form
      autocomplete="off"
      className="form"
      //   ref={form}
      onSubmit={sendEmail}
      id="contact-form"
    >
      <h2>Contact us</h2>
      <div className="fields">
        <div className="field-container">
          <div className="label-container">
            <span className="material-symbols-outlined label-icon">person</span>
          </div>
          <div className="input-container">
            <input
              type="text"
              className="input"
              placeholder="Full Name"
              name="user_name"
            />
          </div>
        </div>
        <div className="field-container">
          <div className="label-container">
            <span className="material-symbols-outlined label-icon">mail</span>
          </div>
          <div className="input-container">
            <input
              type="text"
              className="input"
              placeholder="Email"
              name="user_email"
              id="contact-email"
            />
          </div>
        </div>
        <div className="field-container">
          <div className="label-container">
            <span className="material-symbols-outlined label-icon">call</span>
          </div>
          <div className="input-container">
            <input
              type="number"
              className="input"
              placeholder="Phone"
              id="contact-phone"
              name="phone"
            />
          </div>
        </div>
        <div className="field-container">
          <div className="label-container">
            <span className="material-symbols-outlined label-icon">
              corporate_fare
            </span>
          </div>
          <div className="input-container">
            <input
              type="text"
              className="input"
              placeholder="Organization"
              name="organization"
              id="contact-organization"
            />
          </div>
        </div>
        <div className="textarea">
          <label className="title">Message:</label>
          <textarea name="message" id="message" className="input"></textarea>
        </div>
        <p class="contact__error" id="contact-error"></p>
      </div>
      <input
        type="submit"
        value="Send"
        className="btn btn-secondary"
        onSubmit={contactForm}
      />
    </form>
  );
};
