// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [tasks, setTasks] = useState({
    columnId: {
      title: "Column 1",
      taskIds: ["task1", "task2", "task3"],
    },
    columnId2: {
      title: "Column 2",
      taskIds: ["task4", "task5", "task6"],
    },
    columnId3: {
      title: "Column 3",
      taskIds: ["task7", "task8", "task9"],
    },
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If there's no destination or the task is dropped in the same position, do nothing
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Update the state to reflect the new order of tasks
    const newTasks = { ...tasks };
    const sourceColumn = newTasks[source.droppableId];
    const destinationColumn = newTasks[destination.droppableId];

    sourceColumn.taskIds.splice(source.index, 1);
    destinationColumn.taskIds.splice(destination.index, 0, draggableId);

    setTasks(newTasks);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn columnId="columnId" tasks={tasks} />
        <KanbanColumn columnId="columnId2" tasks={tasks} />
        <KanbanColumn columnId="columnId3" tasks={tasks} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
