import "../assets/scss/_kanban.scss";

const KanbanTask = ({ task }) => {
  return (
    <div className="kanban-task">
      <h3>{task.title}</h3>
      <p>{task.description}</p>
      <p>Assigned to: {task.assignedTo}</p>
    </div>
  );
};

export default KanbanTask;
