// Kanban.js
import React from "react";
import { DragDropContext } from "react-beautiful-dnd";
import KanbanColumn from "./KanbanColumn";

const Kanban = ({ columnsData, onDragEnd }) => {
  // Check if columnsData is undefined or not an object
  if (!columnsData || typeof columnsData !== "object") {
    console.error("Invalid columnsData:", columnsData);
    return null; // or render an error message
  }

  // Check if columnsData has the expected properties
  const { open, ongoing, close } = columnsData;
  if (!open || !ongoing || !close) {
    console.error("Missing properties in columnsData:", columnsData);
    return null; // or render an error message
  }

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" tasks={open} />
        <KanbanColumn id="ongoing" tasks={ongoing} />
        <KanbanColumn id="close" tasks={close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
