import React, { useEffect, useMemo, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet-async";
import { useTranslation } from "react-i18next";

const BlogPost = () => {
  const { i18n } = useTranslation();
  const navigate = useNavigate();
  const { url } = useParams();

  const isHe = i18n.language?.startsWith("he");
  const dir = isHe ? "rtl" : "ltr";
  const lang = isHe ? "he" : "en";
  const otherLang = isHe ? "en" : "he";
  const blogBase = isHe ? "/בלוג/" : "/blog/"; // ✅ בסיס נתיב לפי שפה

  const [post, setPost] = useState(null);
  const [notFound, setNotFound] = useState(false);

  useEffect(() => {
    let cancelled = false;

    const load = async () => {
      try {
        // טען את רשימת הפוסטים בשפה הנוכחית
        const m = await import(`../data/blogs.${lang}.json`);
        const raw = m.default?.blogs ?? [];
        const current = (Array.isArray(raw) ? raw : Object.values(raw)).map(b => ({
          ...b,
          content: Array.isArray(b.content) ? b.content.join("") : b.content,
        }));

        // נסה למצוא לפי ה-slug שנמצא ב-URL
        let found = current.find(b => b.url === url);

        // ❗ אם לא נמצא: נסה לגלות אם זה slug של השפה השניה ולהפנות
        if (!found) {
          try {
            const mOther = await import(`../data/blogs.${otherLang}.json`);
            const rawOther = mOther.default?.blogs ?? [];
            const other = (Array.isArray(rawOther) ? rawOther : Object.values(rawOther));

            // מחפשים את הפוסט בשפה השניה לפי ה-slug שהגיע ב-URL
            const matchInOther = other.find(b => b.url === url);

            // אם יש לכם שדה מזהה משותף (מומלץ!):  { "id": "crm-001", url: "..."}
            if (matchInOther?.id) {
              const counterpart = current.find(b => b.id === matchInOther.id);
              if (counterpart) {
                // מפנים ל-slug הנכון בשפה הנוכחית
                navigate(`${blogBase}${counterpart.url}/`, { replace: true });
                return;
              }
            }
          } catch (_) {
            /* בולעים – זה רק נסיון עזר */
          }
        }

        if (!cancelled) {
          setPost(found || null);
          setNotFound(!found);
        }
      } catch (e) {
        if (!cancelled) setNotFound(true);
      }
    };

    load();
    return () => { cancelled = true; };
  }, [lang, otherLang, blogBase, url, navigate]);

  const html = useMemo(() => ({ __html: post?.content || "" }), [post]);

  if (notFound) {
    return (
      <section dir={dir} className="blog-post">
        <Helmet>
          <title>{isHe ? "הפוסט לא נמצא" : "Post not found"} | SkyzCRM</title>
          <meta httpEquiv="content-language" content={lang} />
        </Helmet>
        <h1>{isHe ? "הפוסט לא נמצא" : "Post not found"}</h1>
        <p><Link to={blogBase}>{isHe ? "חזרה לבלוג" : "Back to blog"}</Link></p>
      </section>
    );
  }

  if (!post) {
    return (
      <section dir={dir} className="blog-post">
        <Helmet>
          <meta httpEquiv="content-language" content={lang} />
        </Helmet>
        <p>{isHe ? "טוען…" : "Loading…"}</p>
      </section>
    );
  }

  const canonical = `${blogBase}${post.url}/`; // ✅ קנוניקל לפי שפה

  return (
    <section dir={dir} className="blog-post">
      <Helmet>
        <title>{post.title} | SkyzCRM</title>
        <meta name="description" content={post.short || post.title} />
        <meta httpEquiv="content-language" content={lang} />
        <link rel="canonical" href={canonical} />
      </Helmet>

      <h1>{post.title}</h1>

      {post.img && (
        <div className="img-container">
          <img className="blog-post-img" src={post.img} alt={post.title} />
        </div>
      )}

      <article className="content" dangerouslySetInnerHTML={html} />

      <p className="back-to-articles">
        <Link to={blogBase}>
          {isHe ? "← חזרה לכל המאמרים" : "← Back to all articles"}
        </Link>
      </p>
    </section>
  );
};

export default BlogPost;
