// KanbanColumn.js
import React from "react";
import { Droppable } from "react-beautiful-dnd";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ id, tasks }) => {
  const statusColumns = {
    todo: tasks.filter((task) => task.status === "todo"),
    inProgress: tasks.filter((task) => task.status === "inProgress"),
    done: tasks.filter((task) => task.status === "done"),
  };

  return (
    <div className="kanban-column">
      <h2>{id}</h2>
      <div className="kanban-status-columns">
        {Object.keys(statusColumns).map((status) => (
          <Droppable key={status} droppableId={`${id}-${status}`}>
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                className="kanban-tasks-list"
              >
                {statusColumns[status].map((task, index) => (
                  <KanbanTask key={task.id} task={task} index={index} />
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </div>
    </div>
  );
};

export default KanbanColumn;
