import React from "react";
import "../assets/scss/_skyz-demo.scss";
import Kanban from "./Kanban";

const Demo = () => {
  const kanbanData1 = {
    columns: {
      columnId: {
        title: "פתוח",
        subtitle: "פוטנציאלי",
        taskIds: ["task1", "task2", "task3"],
      },
      columnId2: {
        title: "ביצוע",
        subtitle: "הצעה",
        taskIds: ["task4", "task5", "task6"],
      },
      columnId3: {
        title: "סגור",
        subtitle: "הזמנה",
        taskIds: ["task7", "task8", "task9"],
      },
    },
    tasks: {
      task1: {
        title: "הזדמנות",
        description: "עדכון שדה מקור מששני בכרטסת לקוחות",
        p: "בזמן תהליך הכנסת הלידים. השדה יהיה מעודכן בהתאם לליד האחרון שהתקבל עבור אותו לקוח",
        date: "12/12/23",
        sum: "$210,00",
        people: "משה, דוד, שלמה",
      },
      task2: {
        title: "הזדמנות",
        description: "שדרוג גולדפלוס",
        p: "שדרוג האפליקציה ומסד הנתונים לגרסה מתקדמת מ-2008. ידרוש גישה מרחוק.",
        date: "1/1/24",
        sum: "$210,00",
        people: `מיטב השקעות בע"מ, סילבינה בצלאל`,
      },
      task4: {
        title: "הזדמנות",
        description: "גולדפלוס/סקייז",
        p: "שדרוג האפליקציה ומסד הנתונים לגרסה מתקדמת מ-2008. ידרוש גישה מרחוק.",
        date: "12/12/24",
        sum: "$210,00",
        people: `אורלי בסביבה ציפורי בע"מ, אורלי סרוסי זהר`,
      },
      task5: {
        title: "הזדמנות",
        description: "התאמות סקייז",
        p: "שדרוג האפליקציה ומסד הנתונים לגרסה מתקדמת מ-2008. ידרוש גישה מרחוק.",
        date: "12/12/24",
        sum: "$210,00",
        people: `אורלי בסביבה ציפורי בע"מ, אורלי סרוסי זהר`,
      },
    },
  };

  const kanbanData2 = {
    columns: {
      columnId: {
        title: "בתהליך",
        subtitle: "פוטנציאלי",
        taskIds: ["task1", "task2", "task3"],
      },
      columnId2: {
        title: "ביצוע",
        subtitle: "הצעה",
        taskIds: ["task4", "task5", "task6"],
      },
      columnId3: {
        title: "סגור",
        subtitle: "הזמנה",
        taskIds: ["task7", "task8", "task9"],
      },
    },
    tasks: {
      task1: { title: "פוטנציאלי", description: "הזדמנות", date: "12/12/23" },
      task2: {
        title: "Task 2",
        description: "Description 2",
        date: "12/14/23",
      },
      task3: {
        title: "Task 3",
        description: "Description 3",
        date: "12/14/23",
      },
    },
  };

  const kanbanData3 = {
    columns: {
      columnId: {
        title: "סגור",
        subtitle: "פוטנציאלי",
        taskIds: ["task1", "task2", "task3"],
      },
      columnId2: {
        title: "ביצוע",
        subtitle: "הצעה",
        taskIds: ["task4", "task5", "task6"],
      },
      columnId3: {
        title: "סגור",
        subtitle: "הזמנה",
        taskIds: ["task7", "task8", "task9"],
      },
    },
    tasks: {
      task1: { title: "הזדמנות", description: "הזדמנות", date: "12/14/23" },
    },
  };

  return (
    <div className="skyz-demo">
      <div className="demo-container">
        <div className="skyz-demo__main">
          <div className="card-container">
            <div className="card">
              <div className="kanban-container">
                <Kanban initialData={kanbanData1} />
                <Kanban initialData={kanbanData2} />
                <Kanban initialData={kanbanData3} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Demo;
