// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [columnsData, setColumnsData] = useState({
    open: [
      { id: "task-1", title: "Task 1", description: "Description for Task 1", assignedTo: "User A", category: "To Do" },
      // ... other tasks
    ],
    ongoing: [
      { id: "task-2", title: "Task 2", description: "Description for Task 2", assignedTo: "User B", category: "In Progress" },
      // ... other tasks
    ],
    close: [
      { id: "task-3", title: "Task 3", description: "Description for Task 3", assignedTo: "User C", category: "Done" },
      // ... other tasks
    ],
  });

  const onDragEnd = (result) => {
    // ... (unchanged)
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" tasks={columnsData.open} />
        <KanbanColumn id="ongoing" tasks={columnsData.ongoing} />
        <KanbanColumn id="close" tasks={columnsData.close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
