// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = ({ data }) => {
  const [columnsData, setColumnsData] = useState({
    open: [
      {
        id: "task-1",
        title: "Task 1",
        description: "Description for Task 1",
        assignedTo: "User A",
        status: "todo",
      },
      // ... other tasks
    ],
    ongoing: [
      {
        id: "task-2",
        title: "Task 2",
        description: "Description for Task 2",
        assignedTo: "User B",
        status: "inProgress",
      },
      // ... other tasks
    ],
    close: [
      {
        id: "task-3",
        title: "Task 3",
        description: "Description for Task 3",
        assignedTo: "User C",
        status: "done",
      },
      // ... other tasks
    ],
  });
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If there's no destination or the task was dropped back into its original position, do nothing
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Get the task list for the source and destination columns
    const sourceTasks = columnsData[source.droppableId];
    const destinationTasks = columnsData[destination.droppableId];

    // Check if sourceTasks and destinationTasks are arrays
    if (!Array.isArray(sourceTasks) || !Array.isArray(destinationTasks)) {
      console.error(`Invalid column data for source or destination.`);
      return;
    }

    // Find the task being dragged
    const draggedTask = sourceTasks.find((task) => task.id === draggableId);

    if (!draggedTask) {
      console.error(`Task with id ${draggableId} not found in source column.`);
      return;
    }

    // If the destination column is different from the source column
    if (destination.droppableId !== source.droppableId) {
      // Remove the task from the source column
      sourceTasks.splice(source.index, 1);

      // Insert the task into the destination column at the specified index
      destinationTasks.splice(destination.index, 0, draggedTask);

      // Update the state with the new task order
      setColumnsData({
        ...columnsData,
        [source.droppableId]: [...sourceTasks],
        [destination.droppableId]: [...destinationTasks],
      });
    } else {
      // If the destination column is the same as the source column, reordering within the same column
      sourceTasks.splice(source.index, 1);
      sourceTasks.splice(destination.index, 0, draggedTask);

      // Update the state with the new task order within the same column
      setColumnsData({
        ...columnsData,
        [source.droppableId]: [...sourceTasks],
      });
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" tasks={columnsData.open} />
        <KanbanColumn id="ongoing" tasks={columnsData.ongoing} />
        <KanbanColumn id="close" tasks={columnsData.close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
