const Kanban = () => {
  const [tasks, setTasks] = useState({
    columns: {
      columnId: {
        title: "פתוח",
        subtitle: "פוטנציאלי",
        taskIds: ["task1-list-1", "task2-list-2", "task3-list-3"],
      },
      columnId2: {
        title: "ביצוע",
        taskIds: ["task4-list-1", "task5-list-2", "task6-list-3"],
      },
      columnId3: {
        title: "סגור",
        taskIds: ["task7-list-1", "task8-list-2", "task9-list-3"],
      },
    },
    tasks: {
      "task1-list-1": { title: "פוטנציאלי 1", description: "הזדמנות", list: 1 },
      "task2-list-2": {
        title: "Task 2",
        description: "Description 2",
        list: 2,
      },
      "task3-list-3": {
        title: "Task 3",
        description: "Description 3",
        list: 3,
      },
      "task4-list-1": {
        title: "Task 4",
        description: "Description 4",
        list: 1,
      },
      "task5-list-2": {
        title: "Task 5",
        description: "Description 5",
        list: 2,
      },
      "task6-list-3": {
        title: "Task 6",
        description: "Description 6",
        list: 3,
      },
      "task7-list-1": {
        title: "Task 7",
        description: "Description 7",
        list: 1,
      },
      "task8-list-2": {
        title: "Task 8",
        description: "Description 8",
        list: 2,
      },
      "task9-list-3": {
        title: "Task 9",
        description: "Description 9",
        list: 3,
      },
    },
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    const newTasks = { ...tasks };
    const sourceColumn = newTasks.columns[source.droppableId];
    const destinationColumn = newTasks.columns[destination.droppableId];

    sourceColumn.taskIds.splice(source.index, 1);
    destinationColumn.taskIds.splice(destination.index, 0, draggableId);

    setTasks(newTasks);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        {Object.keys(tasks.columns).map((columnId) => (
          <KanbanColumn
            key={columnId}
            columnId={columnId}
            tasks={tasks}
            subtitle={tasks.columns[columnId].title}
          />
        ))}
      </div>
    </DragDropContext>
  );
};

export default Kanban;
