// export default KanbanColumn;
// KanbanColumn.js
import React from "react";
import { Droppable, Draggable } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanTask from "./KanbanTask";

const KanbanColumn = ({ columnId, tasks }) => {
  const column = tasks[columnId];

  return (
    <div>
      <h2>title</h2>
      <Droppable droppableId={columnId} type="COLUMN" direction="horizontal">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="kanban-column"
          >
            <Droppable droppableId={`${columnId}-subtitle1`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-subtitle"
                >
                  <h3>Subtitle 1</h3>
                  <div className="kanban-tasks-list">
                    {column.taskIds.map((taskId, index) => (
                      <Draggable
                        key={taskId}
                        draggableId={taskId}
                        index={index}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <KanbanTask taskId={taskId} />
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                </div>
              )}
            </Droppable>

            <Droppable droppableId={`${columnId}-subtitle2`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-subtitle"
                >
                  <h3>Subtitle 2</h3>
                  <div className="kanban-tasks-list">
                    {/* Tasks for the second subtitle go here */}
                    {provided.placeholder}
                  </div>
                </div>
              )}
            </Droppable>

            <Droppable droppableId={`${columnId}-subtitle3`} type="TASK">
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  className="kanban-subtitle"
                >
                  <h3>Subtitle 3</h3>
                  <div className="kanban-tasks-list">
                    {/* Tasks for the third subtitle go here */}
                    {provided.placeholder}
                  </div>
                </div>
              )}
            </Droppable>
          </div>
        )}
      </Droppable>
    </div>
  );
};

export default KanbanColumn;
