import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [tasks, setTasks] = useState({
    columns: {
      columnId: {
        title: "פתוח",
        subtitle: "פוטנציאלי",
        taskIds: ["task1", "task2", "task3"],
      },
      columnId2: {
        title: "ביצוע",
        subtitle: "זכיה",

        taskIds: ["task4", "task5", "task6"],
      },
      columnId3: {
        title: "סגור",
        subtitle: "בוצע",
        taskIds: ["task7", "task8", "task9"],
      },
    },
    tasks: {
      task1: { title: "פוטנציאלי", description: "הזדמנות" },
      task2: { title: "Task 2", description: "Description 2" },
      task3: { title: "Task 3", description: "Description 3" },
      task4: { title: "Task 4", description: "Description 4" },
      task5: { title: "Task 5", description: "Description 5" },
      task6: { title: "Task 6", description: "Description 6" },
      task7: { title: "Task 7", description: "Description 7" },
      task8: { title: "Task 8", description: "Description 8" },
      task9: { title: "Task 9", description: "Description 9" },
    },
  });

  // const onDragEnd = (result) => {
  //   const { destination, source, draggableId } = result;

  //   if (
  //     !destination ||
  //     (destination.droppableId === source.droppableId &&
  //       destination.index === source.index)
  //   ) {
  //     return;
  //   }

  //   const newTasks = { ...tasks };
  //   const sourceColumn = newTasks.columns[source.droppableId];
  //   const destinationColumn = newTasks.columns[destination.droppableId];

  //   sourceColumn.taskIds.splice(source.index, 1);
  //   destinationColumn.taskIds.splice(destination.index, 0, draggableId);

  //   setTasks(newTasks);
  // };
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If no destination or the drag is canceled, return
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    setTasks((prevTasks) => {
      const newTasks = { ...prevTasks };
      const sourceColumn = newTasks.columns[source.droppableId];
      const destinationColumn = newTasks.columns[destination.droppableId];

      // Ensure both source and destination columns exist
      if (sourceColumn && destinationColumn) {
        // Remove the task from the source column
        const removedTask = sourceColumn.taskIds.splice(source.index, 1)[0];

        // Add the task to the destination column
        destinationColumn.taskIds.splice(destination.index, 0, removedTask);

        console.log("New Tasks:", newTasks);
        console.log("Source Column:", sourceColumn);
        console.log("Destination Column:", destinationColumn);

        return newTasks;
      }

      return prevTasks; // Return the previous state if something goes wrong
    });
  };

  return (
    // <DragDropContext onDragEnd={onDragEnd}>
    //   <div className="kanban">
    //     {Object.keys(tasks.columns).map((columnId) => (
    //       <KanbanColumn
    //         key={columnId}
    //         columnId={columnId}
    //         tasks={tasks}
    //         subtitle={tasks.columns[columnId].subtitle}
    //         titleClassName={`title-${columnId}`}
    //       />
    //     ))}
    //   </div>
    // </DragDropContext>
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        {Object.keys(tasks.columns).map((columnId) => (
          <KanbanColumn
            key={columnId}
            columnId={columnId}
            tasks={tasks}
            subtitle={tasks.columns[columnId].title}
            titleClassName={`custom-title-class-${columnId}`}
          />
        ))}
      </div>
    </DragDropContext>
  );
};

export default Kanban;
