import React, { useMemo, useEffect, useState } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet-async";
import { useTranslation } from "react-i18next";

// ייבוא סטטי של שני הקבצים
import heData from "../data/blogs.he.json";
import enData from "../data/blogs.en.json";

const normalize = (raw) => {
  const arr = Array.isArray(raw?.blogs) ? raw.blogs : Array.isArray(raw) ? raw : Object.values(raw || {});
  return arr.map((b) => ({
    ...b,
    content: Array.isArray(b.content) ? b.content.join("") : (b.content || ""),
    id: String(b.id ?? ""),        // עקביות
    url: String(b.url ?? ""),
    title: String(b.title ?? ""),
  }));
};

const BlogPost = () => {
  const { i18n } = useTranslation();
  const isHe = i18n.language?.startsWith("he");
  const dir = isHe ? "rtl" : "ltr";
  const lang = isHe ? "he" : "en";

  const navigate = useNavigate();
  const params = useParams();
  const slug = decodeURIComponent(params.url || "");

  const hePosts = useMemo(() => normalize(heData), []);
  const enPosts = useMemo(() => normalize(enData), []);

  const posts = isHe ? hePosts : enPosts;
  const postsOther = isHe ? enPosts : hePosts;

  const [post, setPost] = useState(null);
  const [notFound, setNotFound] = useState(false);

  useEffect(() => {
    // חיפוש ישיר בסט הפוסטים של השפה הנוכחית
    const found = posts.find((b) => b.url === slug);
    if (found) {
      setPost(found);
      setNotFound(false);
      return;
    }

    // לא נמצא? בדוק אם זה slug של השפה השנייה והעבר אוטומטית ל־slug הנכון
    const inOther = postsOther.find((b) => b.url === slug);
    if (inOther) {
      const counterpart = posts.find((b) => b.id === inOther.id);
      if (counterpart) {
        navigate(`/בלוג/${encodeURIComponent(counterpart.url)}/`, { replace: true });
        return;
      }
    }

    // לא נמצא בכלל
    setPost(null);
    setNotFound(true);
  }, [slug, posts, postsOther, navigate]);

  if (notFound) {
    return (
      <section dir={dir} className="blog-post">
        <Helmet>
          <title>{isHe ? "הפוסט לא נמצא" : "Post not found"} | SkyzCRM</title>
          <meta httpEquiv="content-language" content={lang} />
        </Helmet>
        <h1>{isHe ? "הפוסט לא נמצא" : "Post not found"}</h1>
        <p><Link to="/בלוג/">{isHe ? "חזרה לבלוג" : "Back to blog"}</Link></p>
      </section>
    );
  }

  if (!post) {
    return (
      <section dir={dir} className="blog-post">
        <Helmet>
          <meta httpEquiv="content-language" content={lang} />
        </Helmet>
        <p>{isHe ? "טוען…" : "Loading…"}</p>
      </section>
    );
  }

  return (
    <section dir={dir} className="blog-post">
      <Helmet>
        <title>{post.title} | SkyzCRM</title>
        <meta name="description" content={post.short || post.title} />
        <meta httpEquiv="content-language" content={lang} />
        <link rel="canonical" href={`/בלוג/${post.url}/`} />
      </Helmet>

      <h1>{post.title}</h1>

      {post.img && (
        <div className="img-container">
          <img className="blog-post-img" src={post.img} alt={post.title} />
        </div>
      )}

      <article className="content" dangerouslySetInnerHTML={{ __html: post.content }} />

      <p className="back-to-articles">
        <Link to="/בלוג/">
          {isHe ? "← חזרה לכל המאמרים" : "← Back to all articles"}
        </Link>
      </p>
    </section>
  );
};

export default BlogPost;
