// Kanban.js
import React, { useState } from "react";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [tasks, setTasks] = useState([
    { id: "task-1", content: "Task 1" },
    { id: "task-2", content: "Task 2" },
    // Add more tasks as needed
  ]);

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If there is no destination or the item is dropped in the same position, do nothing
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Reorder the tasks array based on the drag-and-drop result
    const newTasks = Array.from(tasks);
    const [removedTask] = newTasks.splice(source.index, 1);
    newTasks.splice(destination.index, 0, removedTask);

    // Update the state with the new order of tasks
    setTasks(newTasks);
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Droppable droppableId="kanban" direction="horizontal">
        {(provided) => (
          <div
            className="kanban"
            {...provided.droppableProps}
            ref={provided.innerRef}
          >
            <KanbanColumn tasks={tasks} />
            {/* Add more columns as needed */}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );
};

export default Kanban;
