// Kanban.js
import React, { useState } from "react";
import { DragDropContext } from "react-beautiful-dnd";
import "../assets/scss/_kanban.scss";
import KanbanColumn from "./KanbanColumn";

const Kanban = () => {
  const [columnsData, setColumnsData] = useState({
    open: {
      todo: [
        {
          id: "task-1",
          title: "Task 1",
          description: "Description for Task 1",
          assignedTo: "User A",
        },
        // ... other tasks in the "todo" category
      ],
      inProgress: [
        {
          id: "task-2",
          title: "Task 2",
          description: "Description for Task 2",
          assignedTo: "User B",
        },
        // ... other tasks in the "inProgress" category
      ],
      done: [
        {
          id: "task-3",
          title: "Task 3",
          description: "Description for Task 3",
          assignedTo: "User C",
        },
        // ... other tasks in the "done" category
      ],
    },
    ongoing: {
      // ... similar structure for "ongoing" column
    },
    close: {
      // ... similar structure for "close" column
    },
  });

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If there's no destination or the task was dropped back into its original position, do nothing
    if (
      !destination ||
      (destination.droppableId === source.droppableId &&
        destination.index === source.index)
    ) {
      return;
    }

    // Get the task list for the source and destination columns and categories
    const sourceTasks = [
      ...columnsData[source.droppableId][source.droppableCategory],
    ];
    const destinationTasks = [
      ...columnsData[destination.droppableId][destination.droppableCategory],
    ];

    // Find the task being dragged
    const draggedTask = sourceTasks.find((task) => task.id === draggableId);

    if (!draggedTask) {
      console.error(`Task with id ${draggableId} not found in source column.`);
      return;
    }

    // If the destination column or category is different from the source column or category
    if (
      destination.droppableId !== source.droppableId ||
      destination.droppableCategory !== source.droppableCategory
    ) {
      // Remove the task from the source column and category
      sourceTasks.splice(source.index, 1);

      // Insert the task into the destination column and category at the specified index
      destinationTasks.splice(destination.index, 0, draggedTask);

      // Update the state with the new task order
      setColumnsData({
        ...columnsData,
        [source.droppableId]: {
          ...columnsData[source.droppableId],
          [source.droppableCategory]: sourceTasks,
        },
        [destination.droppableId]: {
          ...columnsData[destination.droppableId],
          [destination.droppableCategory]: destinationTasks,
        },
      });
    } else {
      // If the destination column and category are the same as the source column and category, reordering within the same category
      sourceTasks.splice(source.index, 1);
      sourceTasks.splice(destination.index, 0, draggedTask);

      // Update the state with the new task order within the same category
      setColumnsData({
        ...columnsData,
        [source.droppableId]: {
          ...columnsData[source.droppableId],
          [source.droppableCategory]: sourceTasks,
        },
      });
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="kanban">
        <KanbanColumn id="open" tasks={columnsData.open} />
        <KanbanColumn id="ongoing" tasks={columnsData.ongoing} />
        <KanbanColumn id="close" tasks={columnsData.close} />
      </div>
    </DragDropContext>
  );
};

export default Kanban;
